import Classname from "./Classname";
declare class CSVInput<T> {
    submitted: boolean;
    prompt: string;
    classname: Classname<T>;
    callback: (data: T[]) => void;
    dom: HTMLDivElement;
    fileInput: HTMLInputElement;
    okButton: HTMLInputElement;
    constructor(prompt: string, classname: Classname<T>, callback: (value: T[]) => void);
    private initDOM(prompt);
    private initEventHandlers();
    private submit(e);
    private invalidState();
    private submittedState();
    private selected(files);
    private parseRow(row);
}
export default CSVInput;
